import React, { useState } from "react";
import { ReactComponent as AirplaneTilt } from "../assets/budget-icons/airplane-tilt.svg";

function BudgetItem() {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState("nazwa");
  const [money, setMoney] = useState("$");
  const [icon, setIcon] = useState(AirplaneTilt);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    setIsEditing(false);
  };

  const handleCancel = () => {
    setIsEditing(false);
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleMoneyChange = (event) => {
    setMoney(event.target.value);
  };

  const handleIconChange = (event) => {
    // Use the FileReader API to read the selected file and set the icon state
    const fileReader = new FileReader();
    fileReader.readAsDataURL(event.target.files[0]);
    fileReader.onload = (event) => {
      setIcon(() => {
        return <img src={event.target.result} alt="Icon" />;
      });
    };
  };

  return (
    <div className="budget-item">
      <div className="budget-item--icon">{icon}</div>
      <div className="budget-item--text">
        {isEditing ? (
          <div>
            <input
              type="text"
              value={title}
              onChange={handleTitleChange}
            />
            <input type="file" onChange={handleIconChange} />
            <input
              type="text"
              value={money}
              onChange={handleMoneyChange}
            />
            <button onClick={handleSave}>Save</button>
            <button onClick={handleCancel}>Cancel</button>
          </div>
        ) : (
          <div>
            <h3 className="budget-text--title">{title}</h3>
            <p className="budget-text--money">{money}</p>
            <button onClick={handleEdit}>Edit</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default BudgetItem;
