import { useState } from "react";
import ModalInput from "../../01-atoms/inputs/modal-input/ModalInput";
import SaveButton from "../../01-atoms/buttons/save-button/SaveButton";

function ExpenseForm({ onSave, onCancel }) {
  const [title, setTitle] = useState("");
  const [money, setMoney] = useState("");
  const [date, setDate] = useState("");

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleMoneyChange = (e) => {
    setMoney(e.target.value);
  };

  const handleDateChange = (e) => {
    setDate(e.target.value);
  };

  const handleSaveClick = () => {
    const newExpense = {
      id: Math.random().toString(),
      title,
      money,
      date: new Date(date),
    };

    onSave(newExpense);
    setTitle("");
    setMoney("");
    setDate("");
  };

  return (
    <section className="edit-expense-modal">
      <div className="edit-expense-modal--container">
        <h3 className="edit-expense-modal--title">Add New Expense</h3>
        <label className="edit-expense-modal--label">
          Title:
          <ModalInput
            type="text"
            maxLength="18"
            value={title}
            onChange={handleTitleChange}
          />
        </label>
        <label className="edit-expense-modal--label">
          Money:
          <ModalInput
            type="number"
            pattern="[0-9]*"
            maxLength="6"
            value={money}
            onChange={handleMoneyChange}
          />
        </label>
        <label className="edit-expense-modal--label">
          Date:
          <ModalInput
            type="date"
            value={date}
            onChange={handleDateChange}
          />
        </label>
        <div className="edit-expense-modal--buttons">
          <SaveButton onClick={handleSaveClick}>Save</SaveButton>
          <button onClick={onCancel}>Cancel</button>
        </div>
      </div>
    </section>
  );
}

export default ExpenseForm;
