  }
  return (
    <article className="budget-item--container">
      <AirplaneTilt className="budget-item--icon" />
      <div className="budget-items">
        <div className="budget-items--left">
          <h3 className="budget-title">{budgetTitle}</h3>
          <EditButton onClick={handleEditClick}/>
        </div>
        <div className="budget-items--right">
          <p className="budget-money">{budgetMoney} z≈Ç</p>
          <DeleteButton />
        </div>
      </div>
      {isModalOpen && (
        <ModalWindow>
          <h2>Edit {budgetTitle}</h2>

          <label>
            Title:
            <ModalInput type="text" maxLength="18" value={budgetTitle} onChange={handleBudgetTitleChange}/>
          </label>

          <label>
            Budget Amount:
            <ModalInput type="number" pattern="[0-9]*" maxLength="6" value={budgetMoney} onChange={handleBudgetMoneyChange}/>
          </label>
          <SaveButton onClick={handleSaveClick} />
        </ModalWindow>
      )}
    </article>
  );
}

export default BudgetItem;
