import {useState} from "react";

import ExpenseItem from "../../02-molecules/ExpenseItem/ExpenseItem";
import AddButton from "../../01-atoms/buttons/add-button/AddButton";
import CloseButton from "../../01-atoms/buttons/close-button/CloseButton";
import SaveButton from "../../01-atoms/buttons/save-button/SaveButton";

import ModalWindow from "../../02-molecules/ModalWindow/ModalWindow";
import ModalInput from "../../01-atoms/inputs/modal-input/ModalInput";

function ExpenseItemsList() {
  //data
  const expensesDummyData = [
    {
      id: "e1",
      title: "Zakupy spożywcze",
      money: "378",
      date: new Date(2023, 4, 19),
    },
    {id: "e2", title: "Paliwo", money: "242", date: new Date(2023, 4, 1)},
    {
      id: "e3",
      title: "Jedzenie dla kota",
      money: "79",
      date: new Date(2023, 4, 11),
    },
    {
      id: "e4",
      title: "Nowy telefon",
      money: "1200",
      date: new Date(2023, 2, 12),
    },
  ];

  //add new expense
  const [expenses, setExpenses] = useState(expensesDummyData);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAddClick = () => {
    setIsModalOpen(true);
  };

  const handleSaveExpense = (newExpense) => {
    setExpenses((prevExpenses) => [newExpense, ...prevExpenses]);
    setIsModalOpen(false);
  };
  // add new inputs 
  const [title, setTitle] = useState("");
  const [money, setMoney] = useState("");
  const [date, setDate] = useState("");
  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleMoneyChange = (e) => {
    setMoney(e.target.value);
  };

  const handleDateChange = (e) => {
    setDate(e.target.value);
  };

  return (
    <section className="expenses-container">
      <header className="expenses-header">
        <h2 className="expenses-header--title">My Expenses</h2>
        <AddButton onClick={handleAddClick}>Add New Expense</AddButton>
      </header>

      {expenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          money={expense.money}
          date={expense.date}
        />
      ))}

      {isModalOpen && (
        <ModalWindow>
          <CloseButton onClick={() => setIsModalOpen(false)} />
          <h3>
            Add <span>New Item</span>
          </h3>
          <label>
            Title:
            <ModalInput type="text" maxLength="18" value={title} />
          </label>
          <label>
            Money:
            <ModalInput
              type="number"
              pattern="[0-9]*"
              maxLength="6"
              value={money}
            />
          </label>
          <label>
            Data:
            <ModalInput type="date" value={date} />
          </label>
          <SaveButton onClick={handleSaveExpense} />
        </ModalWindow>
      )}
    </section>
  );
}

export default ExpenseItemsList;
